#Copyright (c) 2017 Corwin Hansen#This graphs the capacitor charging data from tektronix tbs 1064 channel 1 starting at trigger point (0 sec)nsample = 5; #number of sample for a single curveformat = '-dsvg';#format for the graph file outputmultiplyer = 10;# voltage multiplyerpath = input('Plese enter the path with capacitor charge data: ', 's'); #get filename with stationary pendulums datafiles = glob(strcat(path, '/*.CSV'));#get all filesfor i=0:numel(files)/nsample-1  for j=1:nsample    voltage{j}=multiplyer*csvread(files{(i*nsample)+j})(:, 5);#read in data for voltage  endfor  time=csvread(files{(i*nsample)+1})(:, 4);#read in data for time  [~, name] = fileparts (files{(i*nsample)+1});#get filename  plotlabel{i+1} = strtok( name, '_');#get the first part of name as label  plot(time, mean(cell2mat(voltage), 2));#plot data of avarage of voltage  hold on;# hold plot for more dataendforgrid on;#add grid to plotlegend(plotlabel);#add legendxlabel('seconds');#label x axisylabel('volt');#label y axistitle(input('Plese enter the graph title: ', 's'));#add title (ex. 40uf Capacitor charge time with 33 coin cell in series)print(strcat(path, '/Capacitor_Charging_Curve'), format);#save graph as file