#Copyright (c) 2017 Corwin Hansen#This graphs the capacitor charging data from tektronix tbs 1064 channel 1 starting at trigger point (0 sec)nsample = 5; #number of sample for a single curveformat = '-dsvg';#format for the graph file outputpath = input('Plese enter the path with capacitor charge data: ', 's'); #get filename with stationary pendulums datafiles = glob(strcat(path, '/*.CSV'));#get all filesfor i=0:numel(files)/nsample-1  for j=1:nsample    data{j}=csvread(files{(i*nsample)+j});#read in that data  endfor  [~, name] = fileparts (files{(i*nsample)+1});#get filename  plotlabel{i+1} = strtok( name, '_');#get the first part of name as label    plot([1, 2], [1, 2]);#plot that data  hold on;# hold plot for more dataendforlegend(plotlabel);#add legendxlabel('seconds');#label x axisylabel('volt');#label y axistitle('40uf Capacitor charge time with 33 coin cell in series');#add titleprint(strcat(path, '/Capacitor_Charging_Curve'), format);#save graph as file